import DegreeFilterOption from './DegreeFilterOption'
import { ButtonToolbar, ButtonGroup, ToggleButton} from 'react-bootstrap'
import { useState } from 'react'


const DegreeFilterCard = ({data, handlePick}) => {

    const [currentPicked, setCurrentPicked] = useState(0);

    return (
        <ButtonToolbar>
            <ButtonGroup className='mr-2 mt-2 ml-2'>
                {data.map((degree) => (
                    <ToggleButton
                    type="checkbox"
                    variant="primary"
                    value={degree.id}
                    checked={currentPicked === degree.id}
                    onChange={(e) => {
                    setCurrentPicked(e.currentTarget.value)
                    handlePick(degree.courses)
                }}
                    >
                        {degree.name}
                    </ToggleButton>
                ))}
            </ButtonGroup>
        </ButtonToolbar>
    )
}

export default DegreeFilterCard
